package notai.recording.application;

import notai.common.domain.vo.FilePath;
import notai.common.exception.type.BadRequestException;
import notai.common.utils.AudioDecoder;
import notai.common.utils.FileManager;
import notai.document.domain.Document;
import notai.document.domain.DocumentRepository;
import notai.recording.application.command.RecordingSaveCommand;
import notai.recording.application.result.RecordingSaveResult;
import notai.recording.domain.Recording;
import notai.recording.domain.RecordingRepository;
import org.junit.jupiter.api.BeforeEach;
import org.junit.jupiter.api.Test;
import org.junit.jupiter.api.extension.ExtendWith;
import org.mockito.InjectMocks;
import org.mockito.Mock;
import org.mockito.Spy;
import org.mockito.junit.jupiter.MockitoExtension;
import org.springframework.test.util.ReflectionTestUtils;

import java.nio.file.Files;
import java.nio.file.Path;
import java.nio.file.Paths;

import static org.junit.jupiter.api.Assertions.*;
import static org.mockito.ArgumentMatchers.any;
import static org.mockito.ArgumentMatchers.anyLong;
import static org.mockito.BDDMockito.given;
import static org.mockito.Mockito.mock;

@ExtendWith(MockitoExtension.class)
class RecordingServiceTest {

    @InjectMocks
    private RecordingService recordingService;

    @Mock
    private RecordingRepository recordingRepository;

    @Mock
    private DocumentRepository documentRepository;

    @Spy
    private final AudioDecoder audioDecoder = new AudioDecoder();

    @Spy
    private final FileManager fileManager = new FileManager();

    @BeforeEach
    void setUp() {
        ReflectionTestUtils.setField(recordingService, "audioBasePath", "src/main/resources/audio/");
    }

    @Test
    void 녹음_파일_업로드시_잘못된_데이터인_경우_예외발생() {
        // given
        Long documentId = 1L;
        String invalidAudioData = "data:audio/mpeg;base64,!!!";

        Document document = mock(Document.class);
        RecordingSaveCommand command = new RecordingSaveCommand(documentId, invalidAudioData);

        Recording savedRecording = new Recording(document);
        ReflectionTestUtils.setField(savedRecording, "id", 1L);

        given(documentRepository.getById(anyLong())).willReturn(document);
        given(recordingRepository.save(any(Recording.class))).willReturn(savedRecording);
        given(document.getName()).willReturn("안녕하세요백종원입니다");

        // when & then
        assertThrows(BadRequestException.class, () -> {
            recordingService.saveRecording(command);
        });
    }

    @Test
    void 녹음_파일_업로드() {
        // given
        Long documentId = 1L;
        String base64AudioData =
                "data:audio/mpeg;base64,";

        Document document = mock(Document.class);
        RecordingSaveCommand command = new RecordingSaveCommand(documentId, base64AudioData);

        Recording savedRecording = new Recording(document);
        ReflectionTestUtils.setField(savedRecording, "id", 1L);

        given(documentRepository.getById(anyLong())).willReturn(document);
        given(recordingRepository.save(any(Recording.class))).willReturn(savedRecording);
        given(document.getName()).willReturn("안녕하세요백종원입니다");

        // when
        RecordingSaveResult result = recordingService.saveRecording(command);

        // then
        FilePath filePath = FilePath.from("안녕하세요백종원입니다_1.mp3");
        Path expectedFilePath = Paths.get("src/main/resources/audio/" + filePath.getFilePath());

        assertAll(() -> assertTrue(Files.exists(expectedFilePath)), () -> assertTrue(Files.size(expectedFilePath) > 0));
    }
}